<?phpnamespace GDO\Net\tpl;use GDO\Net\GDT_Url;/** @var $field GDT_Url * */$var = $field->getVar();$url = $field->getValue();?><span class="gdt-url<?=$var ? '' : ' gdt-empty'?>"><a<?=$field->htmlAttributes()?>		<?php		if ($field->noFollow) : ?>			rel="external nofollow"		<?php		endif; ?>		<?php		if ($var) : ?>			href="<?=html($var)?>"		<?php		endif; ?>><?phpif ($var){	echo $field->hasTitle() ? $field->renderTitle() : $url->getHost();}else{	echo $field->none();}?></a></span>